#!/usr/bin/env python3
# -*- coding: utf-8 -*-

import matplotlib
matplotlib.use('Qt4Agg')
import scipy
import pylab
import scipy.fftpack
import scipy.signal
import sys


# example data from wikipedia
# https://en.wikipedia.org/wiki/Pulse-density_modulation#Examples
pcm_sine1 = "0101011011110111111111111111111111011111101101101010100100100000010000000000000000000001000010010101"
pcm_sine2 = "0101101111111111111101101010010000000000000100010011011101111111111111011010100100000000000000100101"

pcm_test = ""




if __name__ == "__main__":
	print("pdm2pcm.py")

#	signal = pcm_sine1
#	signal = pcm_sine2
#	downsample = 10

	signal = pcm_test
	downsample = 64


	inp = [int(b) for b in signal]#sinyal bir arrayse mesela arrayin tüm elemanlarını integera cast edip yeni bir arraye yazıyor
	out = scipy.signal.decimate(inp, downsample)


	# plot
	pylab.figure(1)
	pylab.subplot(211)
	pylab.plot(out)

	fft = abs(scipy.fft(out))
	freqs = scipy.fftpack.fftfreq(len(out), 1/float(16000))

	pylab.figure(2)
	pylab.subplot(211)
	pylab.plot(freqs, 20*scipy.log10(fft),'x')

	fft = abs(scipy.fft(inp))
	freqs = scipy.fftpack.fftfreq(len(inp), 1/float(16000 * 64))

	pylab.figure(3)
	pylab.subplot(211)
	pylab.plot(freqs, 20*scipy.log10(fft),'x')

	pylab.show()

